import MotionHoc from "./MotionHoc";
import "./Team.css";

const biographyData = [
  {
    title: 'Общее',
    text: `Чингиз Айтматов — советский и киргизский писатель, творчество которого по значимости для народа сопоставимо с эпосом «Манас». Он писал на русском и киргизском языках, объединяя традиции, мифологию и современность. Его произведения стали важным вкладом в мировую литературу.`,
  },
  {
    title: 'Детство и юность',
    text: `Родился 12 декабря 1928 года в селе Шекер Таласской области. Его отец был репрессирован в 1938 году, а мать — учительница. В годы войны юный Чингиз был секретарем аульного совета. После войны окончил зоотехникум и сельхозинститут, но интерес к литературе взял верх.`,
  },
  {
    title: 'Книги и творчество',
    text: `Писать начал в 1952 году. Первая известная повесть — «Джамиля», прославившая его за пределами СССР. Его роман «И дольше века длится день» сочетает реализм и фантастику, а «Плаха» поднимает острые социальные темы. В произведениях часто звучат народные мотивы и легенды — как, например, образ манкурта.`,
  },
  {
    title: 'Личная жизнь',
    text: `Был женат дважды. Первая жена — Керез Шамшибаева, с ней родил двух сыновей. Влюблен был в балерину Бюбюсару Бейшеналиеву, с которой состоял в длительной связи. Вторая жена — Мария Урматовна, от неё у него двое детей. Женщины в его жизни и книгах — всегда сильные, душевные и глубокие образы.`,
  },
  {
    title: 'Смерть и память',
    text: `Айтматов умер 10 июня 2008 года в Германии от пневмонии. Похоронен на мемориальном кладбище Ата-Бейит рядом с отцом. Его вклад в культуру отмечается во всем мире, а его произведения остаются актуальными и читаемыми по сей день.`,
  },
];

const TeamComponent = () => {
  return (
    <div className="team-container">
      <h1 className="team-title">Биография Чингиза Айтматова</h1>

      <div className="photo-wrapper">
        <img
          src="https://retrofm.kg/wp-content/uploads/2014/06/listing_original_13826157326170638335.jpg"
          alt="Чингиз Айтматов"
          className="bio-photo"
        />
        <p className="photo-caption">Чингиз Айтматов</p>
      </div>

      {biographyData.map((section, index) => (
        <section key={index} className="bio-section">
          <h2>{section.title}</h2>
          <p>{section.text}</p>
        </section>
      ))}
    </div>
  );
};


const Team = MotionHoc(TeamComponent);

export default Team;
